matrix = [list(line) for line in input.split("\n")]

def check_MAS(i, j, matrix):
    rows, cols = len(matrix), len(matrix[0])
    
    # Helper function to check if a position is within bounds
    def is_within_bounds(x, y):
        return 0 <= x < rows and 0 <= y < cols

    # Retrieve elements of the top-left to bottom-right diagonal
    tl_br = []
    if is_within_bounds(i - 1, j - 1) and is_within_bounds(i + 1, j + 1):
        tl_br = [matrix[i - 1][j - 1], matrix[i + 1][j + 1]]
    
    # Retrieve elements of the top-right to bottom-left diagonal
    tr_bl = []
    if is_within_bounds(i - 1, j + 1) and is_within_bounds(i + 1, j - 1):
        tr_bl = [matrix[i - 1][j + 1], matrix[i + 1][j - 1]]
    
    # Check if both diagonals contain "M" and "S"
    if "M" in tl_br and "S" in tl_br and "M" in tr_bl and "S" in tr_bl:
        return 1
    return 0

sol = 0
for i, line in enumerate(matrix):
    for j, element in enumerate(line):
        if element != "A":
            continue  # Skip non-"X" elements

        # Check all directions except the (0, 0) stationary direction
        if check_MAS(i, j, matrix):
            sol += 1

print(sol)
